<p-toolbar>
  <div class="p-toolbar-group-left">
    <h1>Aplicación Autorizador</h1>
  </div>
</p-toolbar>
<div class="grid p-8">
  <div class="col w-full">
    <!-- Entrada JSON -->
    <textarea
      pInputTextarea
      class="w-full h-full"
      rows="15"
      cols="30"
      placeholder="Ingresa JSON aquí"
    ></textarea>
  </div>
  <div class="col">
    <!-- Formulario Dinámico -->
    <h2>Formulario Dinámico</h2>

    <p-dropdown
      [options]="formTypes"
      [(ngModel)]="selectedFormType"
      placeholder="Selecciona un tipo"
      class="mb-4"
    ></p-dropdown>

    <div #myForm *ngIf="selectedFormType === 'cuenta'">
      <form [formGroup]="cuentaForm" (ngSubmit)="onSubmit()">
        <div class="flex">
          <p-checkbox
            [binary]="true"
            class="w-full mb-4"
            formControlName="tarjetaActiva"
            label="Tarjeta Activa"
          ></p-checkbox>

          <input
            type="text"
            pInputText
            formControlName="id"
            placeholder="id"
            class="w-full mb-4"
          />
        </div>

        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="limiteDisponible"
          placeholder="Limite disponible"
        />
        <button pButton type="submit">Enviar cuenta</button>
      </form>
    </div>

    <div *ngIf="selectedFormType === 'transaccion'">
      <form [formGroup]="transaccionForm" (ngSubmit)="onSubmit()">
        <input
          type="text"
          pInputText
          class="w-full mb-4 mt-4"
          formControlName="id"
          placeholder="id"
        />
        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="comerciante"
          placeholder="Comerciante"
        />
        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="cantidad"
          placeholder="Cantidad"
        />
        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="tiempo"
          placeholder="Tiempo"
        />
        <button pButton type="submit" label="Enviar Transacción"></button>
      </form>
    </div>
  </div>
</div>

<div class="grid">
  <div class="col-12 p-8">
    <!-- Área de Consola -->
    <p-panel header="Consola de Resultados">
      <!-- iterate over results of type -->
      <!-- 
      {
    "cuenta": {
        "id": 1,
        "tarjeta_activa": true,
        "limite_disponible": 80
    },
    "violaciones": [
        "cuenta-ya-inicializada"
    ]
} -->

      <div *ngFor="let response of apiResponses">
        <h3>Resultado</h3>
        <p>Violaciones: {{ response.violaciones }}</p>
        <p>Resultado: {{ response.Cuenta }}</p>
      </div>
    </p-panel>
  </div>
</div>

<router-outlet></router-outlet>
