<p-toolbar>
  <div class="p-toolbar-group-left">
    <h1>Aplicación Autorizador</h1>
  </div>
  <div class="p-toolbar-group-right">
    <!-- Container a p-toggle to switch beetween console/batch mode and the forms -->
    <p-toggleButton
      [(ngModel)]="consoleMode"
      onLabel="'Modo Consola'"
      offLabel="Modo Batch"
      (onChange)="(toggleMode)"
    ></p-toggleButton>
  </div>
</p-toolbar>
<div class="grid p-8">
  <div class="col w-full" *ngIf="consoleMode">
    <!-- Entrada JSON -->
    <textarea
      pInputTextarea
      [(ngModel)]="jsonData"
      class="w-full h-full"
      rows="15"
      cols="30"
      placeholder="Ingresa JSON aquí"
    ></textarea>
    <button
      pButton
      type="button"
      label="Enviar JSON"
      (click)="processJsonData()"
    ></button>
  </div>
  <div class="col">
    <!-- Formulario Dinámico -->
    <h2>Formulario Dinámico</h2>

    <p-dropdown
      [options]="formTypes"
      [(ngModel)]="selectedFormType"
      placeholder="Selecciona un tipo"
      class="mb-4"
    ></p-dropdown>

    <div #myForm *ngIf="selectedFormType === 'cuenta' && !consoleMode">
      <form [formGroup]="cuentaForm" (ngSubmit)="onSubmit()">
        <div class="flex">
          <p-checkbox
            [binary]="true"
            class="w-full mb-4"
            formControlName="tarjetaActiva"
            label="Tarjeta Activa"
          ></p-checkbox>

          <input
            type="text"
            pInputText
            formControlName="id"
            placeholder="id"
            class="w-full mb-4"
          />
        </div>

        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="limiteDisponible"
          placeholder="Limite disponible"
        />
        <button pButton type="submit">Enviar cuenta</button>
      </form>
    </div>

    <div *ngIf="selectedFormType === 'transaccion' && !consoleMode">
      <form [formGroup]="transaccionForm" (ngSubmit)="onSubmit()">
        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="comerciante"
          placeholder="Comerciante"
        />
        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="cantidad"
          placeholder="Cantidad"
        />
        <p-calendar
          formControlName="tiempo"
          placeholder="Tiempo"
          dateFormat="yy-mm-dd"
          [showTime]="true"
          [hourFormat]="'24'"
        ></p-calendar>
        <button pButton type="submit" label="Enviar Transacción"></button>
      </form>
    </div>
  </div>
</div>

<div class="grid">
  <div class="col-12 p-8" *ngIf="!consoleMode">
    <!-- Área de Consola -->
    <p-panel header="Consola de Resultados">
      <p-divider></p-divider>
      <div *ngFor="let response of apiResponses">
        <p>
          Violaciones:
          <strong class="text-red-500">{{ response.violaciones }}</strong>
        </p>
        <p>Resultado: {{ response.cuenta | json }}</p>
        <p-divider></p-divider>
      </div>
    </p-panel>
  </div>
</div>

<router-outlet></router-outlet>
