import { Component, OnInit } from '@angular/core';
import { ReactiveFormsModule, Validators } from '@angular/forms';
import { FormBuilder, FormGroup } from '@angular/forms';
import { PrimeNGConfig } from 'primeng/api';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  jsonData = '';
  apiResponse = '';
  cuentaForm: FormGroup;
  transaccionForm: FormGroup;
  selectedFormType: string = 'cuenta';
  formTypes: any[];

  constructor(
    private primengConfig: PrimeNGConfig,
    private formBuilder: FormBuilder
  ) {
    this.cuentaForm = this.formBuilder.group({
      id: ['', Validators.required],
      tarjetaActiva: [false, Validators.required],
      limiteDisponible: ['', Validators.required],
    });

    this.transaccionForm = this.formBuilder.group({
      id: ['', Validators.required],
      comerciante: ['', Validators.required],
      cantidad: ['', Validators.required],
      tiempo: ['', Validators.required],
    });

    this.formTypes = [
      { label: 'Cuenta', value: 'cuenta' },
      { label: 'Transacción', value: 'transaccion' },
    ];
  }
  onSubmit() {
    let data;
    if (this.selectedFormType === 'cuenta') {
      data = this.cuentaForm.value;
    } else if (this.selectedFormType === 'transaccion') {
      data = this.transaccionForm.value;
    }

    // Aquí, enviar 'data' a tu servicio API...
  }
  ngOnInit() {
    this.primengConfig.ripple = true;
  }
}
<!-- app.component.html -->
<p-toolbar>
  <div class="p-toolbar-group-left">
    <h1>Aplicación Autorizador</h1>
  </div>
</p-toolbar>

<div class="grid">
  <div class="col w-full">
    <!-- Entrada JSON -->
    <textarea
      pInputTextarea
      class="w-full"
      rows="15"
      cols="30"
      placeholder="Ingresa JSON aquí"
    ></textarea>
  </div>
  <div class="col">
    <!-- Formulario Dinámico -->
    <h2>Formulario Dinámico</h2>
    <p-dropdown
      [options]="formTypes"
      [(ngModel)]="selectedFormType"
      placeholder="Selecciona un tipo"
    ></p-dropdown>

    <div *ngIf="selectedFormType === 'cuenta'">
      <form [formGroup]="cuentaForm" (ngSubmit)="onSubmit()">
        <div class="flex">
          <p-checkbox
            [binary]="true"
            class="w-full mb-4"
            formControlName="tarjetaActiva"
            label="Tarjeta Activa"
          ></p-checkbox>

          <input
            type="text"
            pInputText
            formControlName="id"
            placeholder="id"
            class="w-full mb-4"
          />
        </div>

        <input
          type="text"
          pInputText
          class="w-full mb-4"
          formControlName="limiteDisponible"
          placeholder="Limite disponible"
        />
        <button pButton type="submit" label="Enviar Cuenta"></button>
      </form>
    </div>
    <!-- Transaccion form -->



  </div>
</div>

<div class="grid">
  <div class="col-12">
    <!-- Área de Consola -->
    <p-panel header="Consola de Resultados">
      <p>{{ apiResponse }}</p>
    </p-panel>
  </div>
</div>
<router-outlet></router-outlet>
