import { Component, OnInit } from '@angular/core';
import { ReactiveFormsModule } from '@angular/forms';
import { FormBuilder, FormGroup } from '@angular/forms';
import { PrimeNGConfig } from 'primeng/api';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent implements OnInit {
  [x: string]: any;
  jsonData = '';
  selectedFormType: string = 'cuenta';
  apiResponse = '';
  cuentaForm: FormGroup;
  transaccionForm: FormGroup;
  // selectedFormType = '';

  formTypes = [
    { label: 'Cuenta', value: 'cuenta' },
    { label: 'Transacción', value: 'transaccion' },
  ];
  constructor(
    private primengConfig: PrimeNGConfig,
    private formBuilder: FormBuilder
  ) {
    this.cuentaForm = this.formBuilder.group({
      id: '',
      tarjetaActiva: false,
      limiteDisponible: '',
    });

    this.transaccionForm = this.formBuilder.group({
      id: '',
      comerciante: '',
      cantidad: '',
      tiempo: '',
    });
  }
  onSubmit() {
    let data;
    if (this.selectedFormType === 'cuenta') {
      data = this.cuentaForm.value;
    } else if (this.selectedFormType === 'transaccion') {
      data = this.transaccionForm.value;
    }

    // Aquí, enviar 'data' a tu servicio API...
  }
  ngOnInit() {
    this.primengConfig.ripple = true;
  }
}
<!-- app.component.html -->
<p-toolbar>
  <div class="p-toolbar-group-left">
    <h1>Aplicación Autorizador</h1>
  </div>
</p-toolbar>

<div class="p-grid">
  <div class="p-col-12 p-md-6">
    <!-- Entrada JSON -->
    <textarea
      pInputTextarea
      rows="15"
      cols="30"
      placeholder="Ingresa JSON aquí"
    ></textarea>
  </div>
  <div class="p-col-12 p-md-6">
    <!-- Formulario Dinámico -->
    <p-dropdown
      [options]="formTypes"
      [(ngModel)]="selectedFormType"
      placeholder="Selecciona un tipo"
    ></p-dropdown>
    <!-- Aquí iría el contenido del formulario dinámico -->
    <div *ngIf="selectedFormType === 'cuenta'">
      <form [formGroup]="cuentaForm" (ngSubmit)="onSubmit()">
        <p-inputText
          type="text"
          formControlName="id"
          placeholder="ID"
        ></p-inputText>
        <p-checkbox
          formControlName="tarjetaActiva"
          binary="true"
          label="Tarjeta Activa"
        ></p-checkbox>
        <p-inputText
          type="text"
          formControlName="limiteDisponible"
          placeholder="Límite Disponible"
        ></p-inputText>
        <p-button type="submit" label="Enviar Cuenta"></p-button>
      </form>
    </div>

    <div *ngIf="selectedFormType === 'transaccion'">
      <form [formGroup]="transaccionForm" (ngSubmit)="onSubmit()">
        <p-inputText
          type="text"
          formControlName="id"
          placeholder="ID"
        ></p-inputText>
        <p-inputText
          type="text"
          formControlName="comerciante"
          placeholder="Comerciante"
        ></p-inputText>
        <p-inputText
          type="text"
          formControlName="cantidad"
          placeholder="Cantidad"
        ></p-inputText>
        <p-inputText
          type="text"
          formControlName="tiempo"
          placeholder="Tiempo"
        ></p-inputText>
        <p-button type="submit" label="Enviar Transacción"></p-button>
      </form>
    </div>
  </div>
  <button pButton type="button" label="Enviar Datos"></button>
</div>

<div class="p-grid">
  <div class="p-col-12">
    <!-- Área de Consola -->
    <p-panel header="Consola de Resultados">
      <p>{{ apiResponse }}</p>
    </p-panel>
  </div>
</div>

<router-outlet></router-outlet>
